import React from "react";
import { Link } from "react-router-dom";
// import MainImage from "./../images/project-one/mainimage.png";

const ArticlesList = ({ articles }) => (
  <>
    {articles.map((article, key) => (
      <div key={key} className="mb-5 mt-5">
        <Link  to={`/article/${article.name.replace(/\s+/g, '-').toLowerCase()}/${key + 1}`}>
          <h1> {article.name}</h1>
        </Link>
        <p>{article.subHeading}</p>
        <img width="60%" src={article.mainImage} alt="article one" className="img-fluid" />
      </div>
    ))}
  </>

);

export default ArticlesList;
