import React, { useEffect, useState } from "react";
import TopSection from "./ArticleTopSection/ArticleTopSection";
import Data from "./../pages/article-content";
import { Link } from "react-router-dom";
import Next from "../images/next.svg";

// import { SccrollView, Dimensions } from "react-native";
// import Video from "react-native-video";
import AnimationVideo from "../images/project-two/countdown.mp4";

export default ({ match }) => {
  const name = match.params.name;

  console.log(name);

  const [articleInfo, setArticleInfo] = useState({ upvotes: 0, comments: [] });

  const item1 = Data.find((x) => x.id === "01");
  const item2 = Data.find((x) => x.id === "02");
  const item3 = Data.find((x) => x.id === "03");

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/api/articles/${name}`);
      const body = await result.json();
      setArticleInfo(body);
      // console.log(body);
    };
    fetchData();
  }, [name]);

  return (
    <>
      <TopSection data={item2}></TopSection>
      <div className="article-container">
        <h1 className="project-subhead">Who is Wander for?</h1>
        <p className="wireframe-text">
          Wander is created for two kind of users.{" "}
        </p>
        <div className="row">
          <div className="col-6 two-audience">
            <h1>Ahmed (Male, 30)</h1>
            <p className="wireframe-text">
              Ahmed is planning to go on a trip to Bali and he doesn’t want to
              carry a journal. He wants a way to record his trip.
            </p>
          </div>
          <div className="col-6 two-audience2">
            <h1 className="mr-2">Ahmed (Male, 30)</h1>
            <p className="wireframe-text">
              Ahmed is planning to go on a trip to Bali and he doesn’t want to
              carry a journal. He wants a way to record his trip.
            </p>
          </div>
        </div>
        {/* <div className="background-video">
          <video playsInline loop autoPlay muted className="wander-video">
            <source src={AnimationVideo} type="video/mp4" />
          </video>
        </div> */}
      </div>

      <hr></hr>

      {/* bottom container */}
      <div className="container-xl container-lg">
        <div className="my-container">
          {/* <hr className="upvote-line"></hr> */}

          {/* other articles */}
          {/* article two */}
          <div className="row next-project">
            <div className="col ">
              <h3 className="text-center">Next Project</h3>
              <Link
                to={`/article/${item3.name
                  .replace(/\s+/g, "-")
                  .toLowerCase()}/3`}
              >
                <div className="d-flex justify-content-center">
                  <h1>{item3.name}</h1>
                  <img className="view overlay zoom" src={Next} />
                </div>
              </Link>
            </div>

            {/* <div className="col">
              <h3>Article Three</h3>

              <Link
                to={`/article/${item3.name
                  .replace(/\s+/g, "-")
                  .toLowerCase()}/3`}
              >
                <img
                  className="img-fluid"
                  src={item3.mainImage}
                  alt="article one"
                />
              </Link>
            </div> */}
          </div>
          {/* end of row */}
        </div>
        {/* end of my-container */}
      </div>
    </>
  );
};
