import React from "react";
import Lottie from "react-lottie";
import animation2 from "../lottie/lot-data.json"; //full
import animation1 from "../lottie/lot-data2.json"; //beginning
import ArticlesListPage from "./ArticlesListPage";
import "../sass/style.scss";

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isDivClicked: true,
    };
    //creates a reference for your element to use
    this.myDivToFocus = React.createRef();
  }

  handleOnClick = () => {
    //.current is verification that your element has rendered
    if (this.myDivToFocus.current) {
      this.myDivToFocus.current.scrollIntoView({
        behavior: "smooth",
        inline: "end",
      });
    }
  };
  divHandler() {
    this.setState({
      isDivClicked: false,
    });
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <section className="layout-hero text-left">
            <div className="subheading">
              <hr className="line-landing" />
              <h6 className="intro-sub mt-5">Intro</h6>
            </div>

            <div className="row">
              <div className="lottie-animation col-lg-7.8">
                <div
                  className="clickEvent"
                  onClick={this.divHandler.bind(this)}
                >
                  &nbsp;
                </div>
                {this.state.isDivClicked && (
                  <Lottie
                    options={{
                      loop: true,
                      autoplay: true,
                      animationData: animation1,
                    }}
                  ></Lottie>
                )}
                {!this.state.isDivClicked && (
                  <Lottie
                    options={{
                      loop: false,
                      autoplay: true,
                      animationData: animation2,
                    }}
                  ></Lottie>
                )}
              </div>
            </div>

            <p className="intro-info">
              Fascinated by creating things from scratch <br />
              and transforming ideas into reality.{" "}
            </p>
            <h2
              className="projects-link"
              onClick={this.handleOnClick}
              style={{ cursor: "pointer" }}
            >
              {" "}
              VIEW PROJECTS
            </h2>

            {/* scroll line */}
            <div
              class="scroll-down is-active is-inview"
              scroll-down="home"
              data-controller="opinview"
              op-inview="0, 0.7"
              op-inview-deactivate=""
            >
              <div class="scroll-down__text">
                <span>scroll</span>
              </div>
              <div class="scroll-down__line"></div>
            </div>
          </section>

          <div ref={this.myDivToFocus}>
            <ArticlesListPage />
          </div>
        </div>
      </div>
    );
  }
}

export default HomePage;
