import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import "../../sass/style.scss";

export default withRouter(({ location, data, match }) => {
  const name = match.params.name;
  console.log(name);
  const [articleInfo, setArticleInfo] = useState({ upvotes: 0, comments: [] });
  const [color, setColor] = useState("#fff");

  const colors = {
    "1": "#747FFF",
    "2": "purple",
    "3": "green",
  };

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/api/articles/${name}/upvote`);
      const body = await result.json();
      setArticleInfo(body);
      console.log(body);
    };
    fetchData();
  }, [name]);

  useEffect(() => {
    const { pathname } = location;

    const article = pathname.split("/")[pathname.split("/").length - 1];
    if (colors[article]) {
      setColor(colors[article]);
    } else {
      setColor("#fff");
    }
  }, [location.pathname]);

  return (
    <>
      <div style={{ backgroundColor: color, height: "80vh" }}>
        <div className="container main mb-5 p-5 ">
          {/* row1 */}
          <div className="header-row row">
            {/* column1  */}
            <div className="col-lg-6 col-md-6">
              <h3 className="header-text ">{data.headerText}</h3>
              {/* start of scroll */}
              <div
                className="scroll-down is-active is-inview"
                scroll-down="home"
                data-controller="opinview"
                op-inview="0, 0.7"
                op-inview-deactivate=""
              >
                <div className="scroll-down__line"></div>
              </div>
              <div className="scroll-down__text">
                <span>scroll for more</span>
              </div>
            </div>
            {/* column2 */}

            <div className="col-lg-1 col-md-1"></div>

            <img
              className="mainimage img-fluid col-lg-4 col-md-6"
              src={data.headerImage}
              alt={data.name}
            />
          </div>
          {/* end dof row */}
        </div>
        {/* end of container */}
      </div>
      {/* end of color header */}
      <div className="container">
        <h1 className="project-name text-left">{data.name}</h1>
        <div className="row project-about mt-5">
          <div className="col text-left">
            <p>this post has been upvoted {articleInfo.upvotes} times</p>
            <h6 className="project-subhead">Tools</h6>
            <p className="project-info"> {data.tools}</p>
            <h6 className="project-subhead">Timeline</h6>
            <p className="project-info">{data.timeline}</p>
            <h6 className="project-subhead">My role: </h6>
            <p className="project-info">{data.myRole}</p>
          </div>
          <div className="col text-left">
            <h6 className="project-subhead">Overview</h6>
            <p className="project-info"> {data.overview}</p>
          </div>
        </div>
      </div>
    </>
  );
});
