import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import './style.scss';

export default withRouter(({ location, data }) => {
    const [currentPath, setCurrentPath] = useState(location.pathname);
    const [color, setColor] = useState('#fff');
    const colors = {
      '1': 'cadetblue',
      '2': 'purple',
      '3': 'green'
    }
  
    useEffect(() => {
      const { pathname } = location;
      const article = pathname.split('/')[pathname.split('/').length - 1];
      console.log("New path:", colors[article]);
      if (colors[article]) {
        setColor(colors[article]);
      } else {
        setColor('#fff')
      }
      setCurrentPath(pathname);
    }, [location.pathname]);
  
    return (
        <div className="main mb-5 p-5" style={{ backgroundColor: color }}>
            This is the top Section for each Article
            <h3>{data.name}</h3>
            <p>
                Tools: {data.tools}
            </p>
            <img className="img-fluid" src={data.mainImage} alt={data.name} width="200px" />
        </div>
    )
})
