import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import "../../sass/style.scss";

export default withRouter(({ location, data }) => {
  const [color, setColor] = useState("#fff");
  const colors = {
    "1": "#747FFF",
    "2": "purple",
    "3": "green",
  };

  useEffect(() => {
    const { pathname } = location;
    const article = pathname.split("/")[pathname.split("/").length - 1];
    if (colors[article]) {
      setColor(colors[article]);
    } else {
      setColor("#fff");
    }
  }, [location.pathname]);

  return (
    <>
      <div style={{ backgroundColor: color, height: "80vh" }}>
        <div className="container main mb-5 p-5 ">
          {/* row1 */}
          <div className="header-row row">
            {/* column1  */}
            <div className="col-lg-6 col-md-6">
              <h3 className="header-text ">{data.headerText}</h3>
              {/* start of scroll */}
              <div
                class="scroll-down is-active is-inview"
                scroll-down="home"
                data-controller="opinview"
                op-inview="0, 0.7"
                op-inview-deactivate=""
              >
                <div class="scroll-down__line"></div>
              </div>
              <div class="scroll-down__text">
                <span>scroll for more</span>
              </div>
            </div>
            {/* column2 */}

            <div className="col-lg-1 col-md-1"></div>

            <img
              className="mainimage img-fluid col-lg-4 col-md-6"
              src={data.headerImage}
              alt={data.name}
            />
          </div>
          {/* end dof row */}
        </div>
        {/* end of container */}
      </div>
      {/* end of color header */}
      <div className="container">
        <div className="row">
          <div className="col ">
            <h1 className="">{data.name}</h1>
            <h6 className="">Tools</h6>
            <p className=""> {data.tools}</p>
            <h6 className="">Timeline</h6>
            <p className="">{data.timeline}</p>
            <h6 className="">My role: </h6>
            <p className="">{data.myRole}</p>
          </div>
          <div className="col">
            <h6 className="">Overview</h6>
            <p className=""> {data.overview}</p>
          </div>
        </div>
      </div>
    </>
  );
});
