import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import "../../sass/style.scss";

export default withRouter(({ location, data }) => {
  const [color, setColor] = useState("#fff");
  const colors = {
    "1": "#747FFF",
    "2": "purple",
    "3": "green",
  };

  useEffect(() => {
    const { pathname } = location;
    const article = pathname.split("/")[pathname.split("/").length - 1];
    if (colors[article]) {
      setColor(colors[article]);
    } else {
      setColor("#fff");
    }
  }, [location.pathname]);

  return (
    <>
      <div style={{ backgroundColor: color, height: "150px" }}>
        <div className="container main mb-5 p-5">
          <div className="row">
            <h3 className=" col-6 header-text">{data.headerText}</h3>
            <div className=" col-2"></div>

            <img
              className="mainimage col-3 img-fluid mt-5"
              src={data.headerImage}
              alt={data.name}
            />
          </div>

          {/* end dof row */}
        </div>
        {/* end of container */}
      </div>
      {/* end of color header */}

      <h1>{data.name}</h1>
      <h6>Tools</h6>
      <p> {data.tools}</p>
      <h6>Timeline</h6>
      <p>{data.timeline}</p>
      <h6>My role: </h6>
      <p>{data.myRole}</p>
      <h6>Overview</h6>
      <p> {data.overview}</p>
    </>
  );
});
