import React from "react";
import { Link } from "react-router-dom";
import "../../sass/style.scss";
// import MainImage from "./../images/project-one/mainimage.png";

const ArticlesList = ({ articles }) => (
  <>
    {articles.map((article, key) => (
      <div key={key} className="mb-5 project-image d-flex float-left ">
        <div>
          <Link
            to={`/article/${article.name.replace(/\s+/g, "-").toLowerCase()}/${
              key + 1
            }`}
          >
            <img
              width="100%"
              src={article.mainImage}
              alt="article one"
              className="img-fluid"
            />
          </Link>
        </div>
        <div className="float-left">
          <h1 className="number"> {article.id}</h1>
          <h1 className="name"> {article.name}</h1>
          <h1 className="type"> {article.type}</h1>
          <p className="info">{article.subHeading}</p>
        </div>
      </div>
    ))}
  </>
);

export default ArticlesList;
