import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import "../../sass/style.scss";

export default withRouter(({ location, data }) => {
  const [color, setColor] = useState("#fff");
  const colors = {
    "1": "#747FFF",
    "2": "purple",
    "3": "green",
  };

  useEffect(() => {
    const { pathname } = location;
    const article = pathname.split("/")[pathname.split("/").length - 1];
    if (colors[article]) {
      setColor(colors[article]);
    } else {
      setColor("#fff");
    }
  }, [location.pathname]);

  return (
    <div className="main mb-5 p-5" style={{ backgroundColor: color }}>
      <h3>{data.headerText}</h3>

      <img
        className="img-fluid"
        src={data.mainImage}
        alt={data.name}
        width="200px"
      />
    </div>
  );
});
