import React from "react";
import { Link } from "react-router-dom";
import "../../sass/style.scss";
// import MainImage from "./../images/project-one/mainimage.png";

const ArticlesList = ({ articles }) => (
  <>
    {articles.map((article, key) => (
      <div className="container row">
        <div key={key} className="mb-5 project-image d-flex col-lg-7 col-sm-12">
          <div>
            <Link
              to={`/article/${article.name
                .replace(/\s+/g, "-")
                .toLowerCase()}/${key + 1}`}
            >
              <img
                className="img-fluid "
                // width="650px"
                src={article.mainImage}
                alt="article one"
              />
            </Link>
          </div>
        </div>

        <div className="row">
          <div className="ml-4 float-left text-left col-lg-5 col-sm-12">
            <p className="number"> {article.id}</p>
            <h1 className="name top-left"> {article.name}</h1>
            <p className="type"> {article.type}</p>
            <p className="info">{article.subHeading}</p>
            <Link
              to={`/article/${article.name
                .replace(/\s+/g, "-")
                .toLowerCase()}/${key + 1}`}
            >
              <p className="viewwork ">view work</p>
            </Link>
          </div>
        </div>
      </div>
    ))}
  </>
);

export default ArticlesList;
