import React from "react";
import { Link } from "react-router-dom";
import "../../sass/style.scss";
// import MainImage from "./../images/project-one/mainimage.png";
import { AnimatedBg, Transition } from "scroll-background";

const ArticlesList = ({ articles }) => (
  <>
    {articles.map((article, key) => (
      <div
        key={key}
        className="row each-work float-left "
        style={{ flexDirection: !!((key + 1) % 2) ? "row" : "row-reverse" }}
      >
        <div className="col-xl-8 col-lg-8 work-image">
          <Link
            to={`/article/${article.name.replace(/\s+/g, "-").toLowerCase()}/${
              key + 1
            }`}
          >
            <img
              className="img-fluid"
              src={article.mainImage}
              alt="article one"
            />
          </Link>
        </div>
        <AnimatedBg>
          <div style={{ height: "900px" }} />
          <Transition height="400px" from="#FFFFFF" to="#98D5DE" />
          <div style={{ height: "900px" }} />
          <Transition
            height="400px"
            from="#388E3C"
            to="#FFA000"
            position={0.75}
          >
            <h1>Content that appears within the transition</h1>
          </Transition>
          <div style={{ height: "900px" }} />

          <div className=" col-xl-4  col-lg-4  work-info">
            <p className={!!((key + 1) % 2) ? "number" : "number2"}>
              {" "}
              {article.id}
            </p>
            <h1 className={!!((key + 1) % 2) ? "name" : "name2"}>
              {" "}
              {article.name}
            </h1>
            <p className={!!((key + 1) % 2) ? "type" : "type2"}>
              {" "}
              {article.type}
            </p>
            <div className="row">
              <p
                className={
                  !!((key + 1) % 2)
                    ? "info col-lg-12 col-sm-9  "
                    : "info2 col-lg-12 col-sm-9 "
                }
              >
                {article.subHeading}
              </p>
            </div>

            <Link
              to={`/article/${article.name
                .replace(/\s+/g, "-")
                .toLowerCase()}/${key + 1}`}
            >
              <p className={!!((key + 1) % 2) ? "viewwork" : "viewwork2"}>
                view work
              </p>
            </Link>
          </div>
        </AnimatedBg>
      </div>
    ))}
  </>
);

export default ArticlesList;
