import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

import "../../sass/style.scss";

export default withRouter(({ location, data }) => {
  const [color, setColor] = useState("#fff");
  const colors = {
    "1": "#747FFF",
    "2": "purple",
    "3": "green",
  };

  useEffect(() => {
    const { pathname } = location;
    const article = pathname.split("/")[pathname.split("/").length - 1];
    if (colors[article]) {
      setColor(colors[article]);
    } else {
      setColor("#fff");
    }
  }, [location.pathname]);

  return (
    <>
      <div style={{ backgroundColor: color, height: "80vh" }}>
        <div className="container main mb-5 p-5 ">
          <div className="header-row row">
            <h3 className="header-text col-lg-6 col-md-6">{data.headerText}</h3>
            <div className="col-lg-1 col-md-1"></div>

            <img
              className="mainimage img-fluid col-lg-4 col-md-6  mt-5 "
              src={data.headerImage}
              alt={data.name}
            />
          </div>

          {/* end dof row */}
        </div>
        <div
          class="scroll-down is-active is-inview"
          scroll-down="home"
          data-controller="opinview"
          op-inview="0, 0.7"
          op-inview-deactivate=""
        >
          <div class="scroll-down__text">
            <span>scroll</span>
          </div>
          <div class="scroll-down__line"></div>
        </div>

        {/* end of container */}
      </div>
      {/* end of color header */}

      <h1>{data.name}</h1>
      <h6>Tools</h6>
      <p> {data.tools}</p>
      <h6>Timeline</h6>
      <p>{data.timeline}</p>
      <h6>My role: </h6>
      <p>{data.myRole}</p>
      <h6>Overview</h6>
      <p> {data.overview}</p>
    </>
  );
});
